mapper.insert:
SELECT if (isnull(max(custId)), 1, (MAX(CAST(custId AS signed integer))+1)) from customer
INSERT INTO customer (custId , custNm , mbrId , custTel1 , custTel2 , custTel3 , faxTel1 , faxTel2 , faxTel3 , budAmt , email , reqContent , activeTp , publicYn , frstRegDt , frstRegUser , modfRegDt , modfRegUser
) VALUES ( #{custId} , #{custNm} , #{user.mbrId} , CASE WHEN #{custTel1} = '' THEN NULL ELSE #{custTel1} END , CASE WHEN #{custTel2} = '' THEN NULL ELSE #{custTel2} END , CASE WHEN #{custTel3} = '' THEN NULL ELSE #{custTel3} END , CASE WHEN #{faxTel1} = '' THEN NULL ELSE #{faxTel1} END , CASE WHEN #{faxTel2} = '' THEN NULL ELSE #{faxTel2} END , CASE WHEN #{faxTel3} = '' THEN NULL ELSE #{faxTel3} END , CASE WHEN #{budAmt} = '' THEN 0 ELSE #{budAmt} END , #{email} , #{reqContent} , #{activeTp} , CASE WHEN #{publicYn} = 'Y' THEN 'Y' ELSE 'N' END , now() , #{user.mbrId} , now() , #{user.mbrId} );

mapper.update:
    UPDATE customer
       SET custNm = #{custNm}
		 , custTel1 = (CASE WHEN #{custTel1} = '' THEN NULL ELSE #{custTel1} END)
		 , custTel2 = (CASE WHEN #{custTel2} = '' THEN NULL ELSE #{custTel2} END)
		 , custTel3 = (CASE WHEN #{custTel3} = '' THEN NULL ELSE #{custTel3} END)
	 	 , modfRegDt = now()
		 , modfRegUser = #{user.mbrId}
     WHERE custId = #{custId}

    UPDATE customer
       SET custNm = #{custNm}
		 , custTel1 = (CASE WHEN #{custTel1} = '' THEN NULL ELSE #{custTel1} END)
		 , custTel2 = (CASE WHEN #{custTel2} = '' THEN NULL ELSE #{custTel2} END)
		 , custTel3 = (CASE WHEN #{custTel3} = '' THEN NULL ELSE #{custTel3} END)
		 , faxTel1 = (CASE WHEN #{faxTel1} = '' THEN NULL ELSE #{faxTel1} END)
		 , faxTel2 = (CASE WHEN #{faxTel2} = '' THEN NULL ELSE #{faxTel2} END)
		 , faxTel3 = (CASE WHEN #{faxTel3} = '' THEN NULL ELSE #{faxTel3} END)
		 , budAmt = (CASE WHEN #{budAmt} = '' THEN 0 ELSE #{budAmt} END  )
		 , reqContent =  #{reqContent}
		 , email = #{email}
		 , activeTp =  #{activeTp}
		 , publicYn = (CASE WHEN #{publicYn} = 'Y' THEN 'Y' ELSE 'N' END)
		 , modfRegDt = now()
		 , modfRegUser = #{user.mbrId}
     WHERE custId = #{custId}

mapper.select:
SELECT custId, custId2
  FROM
	(SELECT 1 num, max(custId) custId
   	  FROM customer
   	 WHERE custNm = #{custNm}
   	   AND custTel1 = #{custTel1}
   	   AND custTel2 = #{custTel2}
   	   AND custTel3 = #{custTel3}
	) A, 
	(
	SELECT 1 num,  if (isnull(max(custId)), 1, (MAX(CAST(custId AS signed integer))+1)) custId2
	  FROM customer
	)B 
WHERE A.num = B.num

