mapper.select:
SELECT custId , custNm , custTel1 , custTel2 , custTel3 , faxTel1 , faxTel2 , faxTel3 , budAmt , reqContent , email , activeTp , (select commNm from commcd where commCd = activeTp ) activeTpNm , publicYn , (CASE WHEN publicYn = 'Y' THEN '공동' END) publicYnNm , DATE_FORMAT(frstRegDt, '%Y-%m-%d') as frstRegDt , frstRegUser frstRegNm , (SELECT COUNT(*) FROM customer) totalCnt , mbrId , (SELECT mbrNm FROM member m WHERE m.mbrId = c.mbrId ) mbrNm FROM customer c WHERE 1=1
AND c.mbrId = #{user.mbrId}
AND publicYn = 'Y'
AND activeTp = 'AT001'
AND activeTp = 'AT002'
AND activeTp = 'AT001' AND mbrId in ( SELECT mbrId FROM member m1
SELECT mbrId FROM member m1 WHERE mbrTp in ('MT001','MT002', 'MT003') AND estateCd = #{user.estateCd}
)
AND activeTp = 'AT002' AND mbrId in ( SELECT mbrId FROM member m1
SELECT mbrId FROM member m1 WHERE mbrTp in ('MT001','MT002', 'MT003') AND estateCd = #{user.estateCd}
)
LIMIT #{rowNum}, #{pagePerRow}
