mapper.insert:

        SELECT  if (isnull(max(dirNo)), 1,  (MAX(CAST(dirNo AS signed integer))+1)) from direction 

		INSERT INTO 
			direction (dirNo
				   , workNo
			       , dirContent
			       , regDate
			       , regUser
			       , targetUser
			       )
			values ( #{dirNo}
				  , #{workNo}
				  , #{dirContent}
				  , now()
				  , #{user.mbrId}  
				  , (select mbrId from workdoc w where w.workNo=#{workNo})
			)

mapper.select:
SELECT dirNo , workNo , dirContent , DATE_FORMAT(regDate, '%Y-%m-%d') as regDate , (select mbrNm from member m where d.targetUser = m.mbrId) targetUserNm , (select mbrNm from member m where d.regUser = m.mbrId) regUserNm , (SELECT COUNT(*) FROM direction) totalCnt , (select workTitle from workdoc w where w.workNo = d.workNo) workTitle , (select workContent from workdoc w where w.workNo = d.workNo) workContent , isDone FROM direction d WHERE 1=1
AND regUser=#{user.mbrId}
AND targetUser=#{user.mbrId}
AND isDone='N' order by regDate desc

mapper.update:
    UPDATE direction
       SET dirContent = #{dirContent}
		 , regDate = now()
		 , regUser = #{user.mbrId}
    WHERE dirNo = #{dirNo}

UPDATE direction
   SET isDone = #{isDone}
WHERE dirNo = #{dirNo}
