mapper.insert:
      
        SELECT   if ( isnull(buildCd), 1, MAX(CAST(buildCd AS signed integer))+1)) from building      
      
		INSERT INTO 
			building (buildCd
				  , buildNm
				  , objtTp
				  , roadAddrPart1
				  , jibunAddr
				  , addrDetail
				  , zipNo
				  , frstRegDt
				  , frstRegUser
				  , modfRegDt
				  , modfRegUser
				  )
		VALUES  (
        			#{buildCd}
				  , #{buildNm}
				  , #{objtTp}
				  , #{roadAddrPart1}
				  , #{jibunAddr}
				  , #{addrDetail}
				  , #{zipNo}
				  , now()
				  , #{mbrId}
				  , now()
				  , #{mbrId}
				  
        	);

mapper.update:
    UPDATE building
       SET buildNm = #{buildNm}
       	 , objtTp = #{objtTp}
       	 , roadAddrPart1 = #{roadAddrPart1}
		 , jibunAddr = #{jibunAddr}
		 , addrDetail = #{addrDetail}
		 , zipNo = #{zipNo}
		 , modfRegDt = now()
		 , modfRegUser = #{mbrId}
     WHERE buildCd = #{buildCd}
