mapper.insert:
SELECT ifnull (MAX(CAST(custId AS signed integer))+1,1) from customer
INSERT INTO customer (custId , custNm , mbrId , custTel1 , custTel2 , custTel3 , homeTel1 , homeTel2 , homeTel3 , orgnTel1 , orgnTel2 , orgnTel3 , faxTel1 , faxTel2 , faxTel3 , gradeTp , budAmt , reqContent , feature , jeobsu , buyCond , dtlContent , mbrMemo , activeTp , publicYn , contactYn , explorYn , talkYn , searchYn , visitYn , meetYn , frstRegDt , frstRegUser , modfRegDt , modfRegUser
) VALUES ( #{custId} , #{custNm} , #{mbrId} , CASE WHEN #{custTel1} = '' THEN NULL ELSE #{custTel1} END , CASE WHEN #{custTel2} = '' THEN NULL ELSE #{custTel2} END , CASE WHEN #{custTel3} = '' THEN NULL ELSE #{custTel3} END , CASE WHEN #{homeTel1} = '' THEN NULL ELSE #{homeTel1} END , CASE WHEN #{homeTel2} = '' THEN NULL ELSE #{homeTel2} END , CASE WHEN #{homeTel3} = '' THEN NULL ELSE #{homeTel3} END , CASE WHEN #{orgnTel1} = '' THEN NULL ELSE #{orgnTel1} END , CASE WHEN #{orgnTel2} = '' THEN NULL ELSE #{orgnTel2} END , CASE WHEN #{orgnTel3} = '' THEN NULL ELSE #{orgnTel3} END , CASE WHEN #{faxTel1} = '' THEN NULL ELSE #{faxTel1} END , CASE WHEN #{faxTel2} = '' THEN NULL ELSE #{faxTel2} END , CASE WHEN #{faxTel3} = '' THEN NULL ELSE #{faxTel3} END , #{gradeTp} , CASE WHEN #{budAmt} = '' THEN 0 ELSE #{budAmt} END , #{reqContent} , #{feature} , #{jeobsu} , #{buyCond} , #{dtlContent} , #{mbrMemo} , #{activeTp} , CASE WHEN #{publicYn} = 'Y' THEN 'Y' ELSE 'N' END , CASE WHEN #{contactYn} = 'Y' THEN 'Y' ELSE 'N' END , CASE WHEN #{explorYn} = 'Y' THEN 'Y' ELSE 'N' END , CASE WHEN #{talkYn} = 'Y' THEN 'Y' ELSE 'N' END , CASE WHEN #{searchYn} = 'Y' THEN 'Y' ELSE 'N' END , CASE WHEN #{visitYn} = 'Y' THEN 'Y' ELSE 'N' END , CASE WHEN #{meetYn} = 'Y' THEN 'Y' ELSE 'N' END , now() , #{mbrId} , now() , #{mbrId} );

mapper.update:
    UPDATE customer
       SET custNm = #{custNm}
		 , custTel1 = (CASE WHEN #{custTel2} = '' THEN NULL ELSE #{custTel1} END)
		 , custTel2 = (CASE WHEN #{custTel2} = '' THEN NULL ELSE #{custTel2} END)
		 , custTel3 = (CASE WHEN #{custTel3} = '' THEN NULL ELSE #{custTel3} END)
		 , homeTel1 = (CASE WHEN #{homeTel1} = '' THEN NULL ELSE #{homeTel1} END)
		 , homeTel2 = (CASE WHEN #{homeTel2} = '' THEN NULL ELSE #{homeTel2} END)
		 , homeTel3 = (CASE WHEN #{homeTel3} = '' THEN NULL ELSE #{homeTel3} END)
		 , orgnTel1 = (CASE WHEN #{orgnTel1} = '' THEN NULL ELSE #{orgnTel1} END)
		 , orgnTel2 = (CASE WHEN #{orgnTel2} = '' THEN NULL ELSE #{orgnTel2} END)
		 , orgnTel3 = (CASE WHEN #{orgnTel3} = '' THEN NULL ELSE #{orgnTel3} END)
		 , faxTel1 = (CASE WHEN #{faxTel1} = '' THEN NULL ELSE #{faxTel1} END)
		 , faxTel2 = (CASE WHEN #{faxTel2} = '' THEN NULL ELSE #{faxTel2} END)
		 , faxTel3 = (CASE WHEN #{faxTel3} = '' THEN NULL ELSE #{faxTel3} END)
		 , gradeTp = #{gradeTp}
		 , budAmt = (CASE WHEN #{budAmt} = '' THEN 0 ELSE #{budAmt} END  )
		 , reqContent =  #{reqContent}
		 , feature = #{feature}
		 , jeobsu = #{jeobsu}
		 , buyCond = #{buyCond}
		 , dtlContent =  #{dtlContent}
		 , mbrMemo = #{mbrMemo}
		 , activeTp =  #{activeTp}
		 , publicYn = (CASE WHEN #{publicYn} = 'Y' THEN 'Y' ELSE 'N' END)
		 , contactYn = (CASE WHEN #{contactYn} = 'Y' THEN 'Y' ELSE 'N' END) 
		 , explorYn = (CASE WHEN #{explorYn} = 'Y' THEN 'Y' ELSE 'N' END )
		 , talkYn = (CASE WHEN #{talkYn} = 'Y' THEN 'Y' ELSE 'N' END )
		 , searchYn = (CASE WHEN #{searchYn} = 'Y' THEN 'Y' ELSE 'N' END) 
		 , visitYn = (CASE WHEN #{visitYn} = 'Y' THEN 'Y' ELSE 'N' END )
		 , meetYn = (CASE WHEN #{meetYn} = 'Y' THEN 'Y' ELSE 'N' END )
		 , modfRegDt = now()
		 , modfRegUser = #{mbrId}
		 , mbrId = #{mbrId}
     WHERE custId = #{custId}

mapper.select:
SELECT custId, custId2
  FROM
	(SELECT 1 num, max(custId) custId
   	  FROM customer
   	 WHERE custNm = #{custNm}
   	   AND custTel1 = #{custTel1}
   	   AND custTel2 = #{custTel2}
   	   AND custTel3 = #{custTel3}
	) A, 
	(
	SELECT 1 num,  ifnull (MAX(CAST(custId AS signed integer))+1,1) custId2
	  FROM customer
	)B 
WHERE A.num = B.num

mapper.insert:
INSERT INTO 
	customer (custId
	       , mbrId
	       , custNm
	       , custTel1
	       , custTel2
	       , custTel3)
	values (
		   #{custId}
		  , #{user.mbrId}
		  , #{custNm}
		  , #{custTel1}
		  , #{custTel2}
		  , #{custTel3}
	)

mapper.select:
SELECT custId , custNm , custTel1 , custTel2 , custTel3 , homeTel1 , homeTel2 , homeTel3 , orgnTel1 , orgnTel2 , orgnTel3 , faxTel1 , faxTel2 , faxTel3 , gradeTp , budAmt , reqContent , feature , jeobsu , buyCond , dtlContent , mbrMemo , activeTp , (select commNm from commcd where commCd = activeTp ) activeTpNm , publicYn , (CASE WHEN publicYn = 'Y' THEN '공동' END) publicYnNm , (CASE WHEN contactYn = 'Y' THEN '계약상황' END)|| (CASE WHEN explorYn = 'Y' THEN ' 답사추진 ' END) || (CASE WHEN talkYn = 'Y' THEN '상담중' END) || (CASE WHEN searchYn = 'Y' THEN '물건검색' END) || (CASE WHEN visitYn = 'Y' THEN '방문예정' END) || (CASE WHEN meetYn = 'Y' THEN '미팅예정' END) custState , DATE_FORMAT(frstRegDt, '%Y-%m-%d') as frstRegDt , frstRegUser frstRegNm , (SELECT COUNT(*) FROM customer) totalCnt , mbrId FROM customer WHERE 1=1
AND publicYn = 'Y'
AND activeTp = 'AT001' AND frstRegUser in ( SELECT mbrId FROM member m1
SELECT mbrId FROM member m1 WHERE mbrTp in ('MT001','MT002', 'MT003') AND estateCd = #{user.estateCd}
)
AND activeTp = 'AT002' AND frstRegUser in ( SELECT mbrId FROM member m1
SELECT mbrId FROM member m1 WHERE mbrTp in ('MT001','MT002', 'MT003') AND estateCd = #{user.estateCd}
)
LIMIT #{rowNum}, #{pagePerRow}
