mapper.insert:

        SELECT  if (isnull(max(dirNo)), 1,  (MAX(CAST(dirNo AS signed integer))+1)) from direction 

		INSERT INTO 
			direction (dirNo
				   , workNo
			       , dirContent
			       , regDate
			       , regUser
			       , targetUser
			       )
			values ( #{dirNo}
				  , #{workNo}
				  , #{dirContent}
				  , now()
				  , #{user.mbrId}  
				  , #{targetUser}
			)

mapper.update:
    UPDATE direction
       SET dirContent = #{dirContent}
		 , regDate = now()
		 , regUser = #{user.mbrId}
    WHERE dirNo = #{dirNo}
