mapper.insert:

        SELECT  ifnull (MAX(CAST(taskDocId AS signed integer))+1,1) from taskdoc       

		INSERT INTO 
			taskdoc (taskDocId
			       , mbrId
			       , taskCont
			       , tomoAdver
			       , tomoSaleObj
			       , tomoBuyer
			       , tomoEtc
			       , prob
			       , sugg
			       , frstRegDt
			       , frstRegUser
			       , modfRegDt
			       , modfRegUser
			       )
			values ( #{taskDocId}
				  , #{user.mbrId}
				  , #{taskCont}
				  , #{tomoAdver}
				  , #{tomoSaleObj}
				  , #{tomoBuyer}
				  , #{tomoEtc}
				  , #{prob}
				  , #{sugg}
				  , now()
				  , #{user.mbrId}
				  , now()
				  , #{user.mbrId}
			)

mapper.update:
    UPDATE taskdoc
       SET mbrId = #{user.mbrId}
		 , taskCont = #{taskCont}
		 , tomoAdver =  #{tomoAdver}
		 , tomoSaleObj = #{tomoSaleObj}
		 , tomoBuyer = #{tomoBuyer}
		 , tomoEtc =  #{tomoEtc}
		 , prob =  #{prob}
		 , sugg =  #{sugg}
		 , modfRegDt = now()
		 , modfRegUser = #{user.mbrId}
    WHERE taskDocId = #{taskDocId}
