mapper.insert:

        SELECT  ifnull (MAX(CAST(memoDocId AS signed integer))+1,1) from memodoc 

		INSERT INTO 
			memodoc (memoDocId
			       , mbrId
			       , memoSbj
			       , memoCont
			       , frstRegDt
			       , frstRegUser
			       , modfRegDt
			       , modfRegUser
			       )
			values ( #{memoDocId}
				  ,  #{user.mbrId}  
				  , #{memoSbj}
				  , #{memoCont}
				  , now()
				  , #{user.mbrId}  
				  , now()
				  , #{user.mbrId}  
			)

mapper.update:
    UPDATE memodoc
       SET mbrId = #{user.mbrId}
		 , memoSbj = #{memoSbj}
		 , memoCont =  #{memoCont}
		 , modfRegDt = now()
		 , modfRegUser = #{user.mbrId}
    WHERE memoDocId = #{memoDocId}
