mapper.insert:
INSERT INTO 
	notice (mbrId
	       , ntSbj
	       , ntConts
	       , frstRegUser
	       , frstRegDt
	       , modfRegUser
	       , modfRegDt
	       )
	values ( #{user.mbrId}
		  , #{ntSbj}
		  , #{ntConts}
		  , #{user.mbrId}
		  , now()
		  , #{user.mbrId}
		  , now()
	)

mapper.update:
    UPDATE notice
       SET mbrId = #{user.mbrId}
		 , ntSbj = #{ntSbj}
		 , ntConts =  #{ntConts}
    WHERE noticeId = #{noticeId}

UPDATE notice
	SET viewCnt = viewCnt+1
	WHERE noticeId = #{noticeId}
