mapper.update:
     UPDATE saleobject
       SET objtNm = #{objtNm}
		 , custId = #{custId}
		 , buildCd = #{buildCd}
		 , businessNm =  #{businessNm}
		 , area = (CASE WHEN #{area} = '' THEN 0 ELSE #{area} END)
		 , realArea = ( CASE WHEN #{realArea} = '' THEN 0 ELSE #{realArea} END)
		 , saleTp = #{saleTp} 
		 , bargainAmt = (CASE WHEN #{bargainAmt} = '' THEN 0 ELSE #{bargainAmt} END )
		 , depositAmt = ( CASE WHEN #{depositAmt} = '' THEN 0 ELSE #{depositAmt} END  )
		 , monthlyAmt = (CASE WHEN #{monthlyAmt} = '' THEN 0 ELSE #{monthlyAmt} END)
		 , dong =  #{dong}
		 , floor = (CASE WHEN #{floor} = '' THEN NULL ELSE #{floor} END )
		 , directionTp = #{directionTp}
		 , dueDt =  #{dueDt}
		 , roomCnt = ( CASE WHEN #{roomCnt} = '' THEN NULL ELSE #{roomCnt} END )
		 , bathCnt = (CASE WHEN #{bathCnt} = '' THEN NULL ELSE #{bathCnt} END )
		 , ondolYn = #{ondolYn} 
		 , availableTp = #{availableTp}
		 , availableDt = (CASE WHEN #{availableDt} = '' THEN NULL ELSE #{availableDt} END) 
		 , conditionTp =  #{conditionTp}
		 , memo =  #{memo}
		 , activeTp =  #{activeTp}
		 , publicYn = (CASE WHEN #{publicYn} = 'Y' THEN 'Y' ELSE 'N' END)
		 , advertiseYn = (CASE WHEN #{advertiseYn} = 'Y' THEN 'Y' ELSE 'N' END)
		 , parcelArea = (CASE WHEN #{parcelArea} = '' THEN 0 ELSE #{parcelArea} END)
		 , rightAmt = (CASE WHEN #{rightAmt} = '' THEN 0 ELSE #{rightAmt} END  )
		 , manageAmt = ( CASE WHEN #{manageAmt} = '' THEN 0 ELSE #{manageAmt} END  )
		, interiorYn = (CASE WHEN #{interiorYn} = '' THEN NULL ELSE #{interiorYn} END )
		, surtaxYn = (CASE WHEN #{surtaxYn} = '' THEN NULL ELSE #{surtaxYn} END )
		, parcelAmt = (CASE WHEN #{parcelAmt} = '' THEN 0 ELSE #{parcelAmt} END )
		, premiumAmt = (CASE WHEN #{premiumAmt} = '' THEN 0 ELSE #{premiumAmt} END )
		 , modfRegDt = now()
		 , modfRegUser = #{user.mbrId}
     WHERE objtNo = #{objtNo}
       AND objtTp = #{objtTp}

mapper.delete:
DELETE FROM saleobject 
WHERE objtNo = #{objtNo}
  AND objtTp = #{objtTp}

mapper.select:
SELECT a.objtTp, a.saleTp, ifnull(cntSaleTp, 0) cntSaleTp FROM ( SELECT a.commCd objtTp, b.commCd saleTp FROM commCd a, commCd b where a.groupCd = 'OT' and b.groupCd = 'ST' ) AS A LEFT OUTER JOIN (SELECT objtTp, saleTp, count(saleTp) cntSaleTp FROM saleobject obj WHERE 1=1
AND publicYn = 'Y'
AND obj.activeTp = 'AT001' AND obj.frstRegUser in ( SELECT mbrId FROM member m1
SELECT mbrId FROM member m1 WHERE mbrTp in ('MT001','MT002', 'MT003') AND estateCd = #{user.estateCd}
)
AND obj.activeTp = 'AT002' AND obj.frstRegUser in ( SELECT mbrId FROM member m1
SELECT mbrId FROM member m1 WHERE mbrTp in ('MT001','MT002', 'MT003') AND estateCd = #{user.estateCd}
)
GROUP BY objtTp, saleTp ) AS B ON A.objtTp = B.objtTp AND A.saleTp = B.saleTp ORDER BY a.objtTp, a.saleTp

SELECT DATE_FORMAT(frstRegDt, '%Y-%m-%d') as frstRegDt , objtNo , objtTp , objtNm , buildCd , (select buildNm from building b where b.buildCd = obj.buildCd ) buildNm , CAST(area AS signed integer) area , CAST(realArea AS signed integer) realArea , dong , floor , directionTp , (select commNm from commcd where commCd = directionTp ) directionTpNm , bargainAmt , concat(depositAmt , '/' , monthlyAmt) depositAmt , rightAmt , ifnull (ondolYn, 'N') ondolYn , conditionTp , (select commNm from commcd where commCd = conditionTp ) conditionTpNm , frstRegUser , activeTp , (select commNm from commcd where commCd = activeTp ) activeTpNm , surtaxYn , advertiseYn , publicYn , availableTp , DATE_FORMAT(availableDt, '%Y-%m-%d') as availableDt , parcelAmt , premiumAmt , concat((CASE WHEN advertiseYn = 'Y' THEN '광고' END) , (CASE WHEN advertiseYn = 'Y' THEN ','END) , (CASE WHEN publicYn = 'Y' THEN '공동' END)) etc , frstRegUser frstRegNm , (SELECT COUNT(*) FROM estate.saleobject WHERE objtTp = #{objtTp} and saleTp = #{saleTp} ) totalCnt FROM estate.saleobject obj WHERE objtTp = #{objtTp} AND saleTp = #{saleTp}
AND publicYn = 'Y'
AND obj.activeTp = 'AT001' AND obj.frstRegUser in ( SELECT mbrId FROM member m1
SELECT mbrId FROM member m1 WHERE mbrTp in ('MT001','MT002', 'MT003') AND estateCd = #{user.estateCd}
)
AND obj.activeTp = 'AT002' AND obj.frstRegUser in ( SELECT mbrId FROM member m1
SELECT mbrId FROM member m1 WHERE mbrTp in ('MT001','MT002', 'MT003') AND estateCd = #{user.estateCd}
)
LIMIT #{rowNum}, #{pagePerRow}
