mapper.insert:

        SELECT  if (isnull(max(workNo)), 1,  (MAX(CAST(workNo AS signed integer))+1)) from workdoc 

		INSERT INTO 
			workdoc (workNo
			       , mbrId

			       , custId


			       , objtNo

			       , workTitle
			       , workContent
			       , endDateYn
			       , endDt
			       , frstRegDt
			       , frstRegUser
			       , modfRegDt
			       , modfRegUser
			       , scheduledDt
			       )
			values ( #{workNo}
				  , #{user.mbrId}  

			       , #{custId}


			       , #{objtNo}

			      , #{workTitle}
			      , #{workContent}
			      , #{endDateYn}
			      , CASE WHEN #{endDt} = '' THEN NULL ELSE #{endDt} END
				  , now()
				  , #{user.mbrId}  
				  , now()
				  , #{user.mbrId}  
			      , CASE WHEN #{scheduledDt} = '' THEN now() ELSE #{scheduledDt} END				  
			)

mapper.select:
SELECT workNo , mbrId , custId , objtNo , workTitle , workContent , endDateYn , DATE_FORMAT(endDt, '%Y-%m-%d') as endDt , DATE_FORMAT(frstRegDt, '%Y-%m-%d %I:%i:%s') as frstRegDt , (select mbrNm from member m where w.frstRegUser = m.mbrId) mbrNm , frstRegUser , DATE_FORMAT(scheduledDt, '%Y-%m-%d') as scheduledDt , case when #{user.mbrTp} = 'MT004' then 'Y' ELSE ( case when w.frstRegUser = #{user.mbrId} then 'Y' else 'N' END ) END deleteBtnYn FROM workdoc w WHERE 1=1
AND custId = #{custId}
AND objtNo = #{objtNo}
AND frstRegUser in ( SELECT mbrId FROM member m1
SELECT mbrId FROM member m1 WHERE mbrTp in ('MT001','MT002', 'MT003') AND estateCd = #{user.estateCd}
) order by frstRegDt desc

mapper.update:
    UPDATE workdoc
       SET workTitle = #{workTitle}
         , workContent = #{workContent}
		 , endDateYn = #{endDateYn}
		 , endDt = (CASE WHEN #{endDt} = '' THEN NULL ELSE #{endDt} END) 
		 , modfRegDt = now()
		 , modfRegUser = #{user.mbrId}
		 , scheduledDt = (CASE WHEN #{scheduledDt} = '' THEN frstRegDt ELSE #{scheduledDt} END) 
    WHERE workNo = #{workNo}

mapper.select:
SELECT o.objtNo , o.objtNm , o.objtTp , (select commNm from commcd where commCd = o.objtTp ) objtTpNm , o.saleTp , (select commNm from commcd where commCd = o.saleTp ) saleTpNm
, b.roadAddrPart1 addr
, b.jibunAddr addr
, CAST(area AS signed integer) area , CAST(bargainAmt AS signed integer) bargainAmt , CAST(depositAmt AS signed integer) depositAmt , CAST(monthlyAmt AS signed integer) monthlyAmt FROM saleobject o left outer join customer c on o.custId = c.custId left outer join building b on o.buildCd = b.buildCd WHERE 1=1 AND o.objtNo not in (select objtNo from interobject where custId = #{custId})
AND o.objtNm like concat('%',#{objtNm},'%')
AND o.objtTp = #{objtTp}
AND o.saleTp = #{saleTp}
AND b.roadAddrPart1||' '|| b.addrDetail like concat('%',#{addr},'%')
AND b.jibunAddr||' '|| b.addrDetail like concat('%',#{addr},'%')
AND CAST(area AS signed integer) between #{fromArea} and #{toArea}
AND CAST(bargainAmt AS signed integer) between #{fromBargainAmt} and #{toBargainAmt}
AND CAST(depositAmt AS signed integer) between #{fromDepositAmt} and #{toDepositAmt}
AND CAST(monthlyAmt AS signed integer) between #{fromMonthlyAmt} and #{toMonthlyAmt}
AND c.custNm = #{custNm}
AND CONCAT(c.custTel1,c.custTel2,c.custTel3) like CONCAT('%',#{custTel1},#{custTel2},#{custTel3},'%')

mapper.insert:

        SELECT  if (isnull(max(interNo)), 1,  (MAX(CAST(interNo AS signed integer))+1)) from interobject 

		INSERT INTO 
			interobject (interNo
			       , objtNo
			       , custId
			       , frstRegDt
			       , frstRegUser
			       , modfRegDt
			       , modfRegUser
			       )
			values ( #{interNo}
				  , #{objtNo}
				  , #{custId}
				  , now()
				  , #{user.mbrId}  
				  , now()
				  , #{user.mbrId}  
			)

mapper.select:
SELECT workNo , mbrId , custId , if (isnull(w.objtNo), '', (select objtTp from saleobject o where w.objtNo = o.objtNo )) objtTp , if (isnull(w.objtNo), '', (select saleTp from saleobject o where w.objtNo = o.objtNo )) saleTp , objtNo , workTitle , workContent , endDateYn , DATE_FORMAT(endDt, '%Y-%m-%d') as endDt , DATE_FORMAT(frstRegDt, '%Y-%m-%d %I:%i:%s') as frstRegDt , (select mbrNm from member m where w.frstRegUser = m.mbrId) mbrNm , frstRegUser , DATE_FORMAT(scheduledDt, '%Y-%m-%d') as scheduledDt , case when #{user.mbrTp} = 'MT004' then 'Y' ELSE ( case when w.frstRegUser = #{user.mbrId} then 'Y' else 'N' END ) END deleteBtnYn FROM workdoc w WHERE DATE_FORMAT(scheduledDt, '%Y-%m-%d') = #{selectedDate} AND frstRegUser in ( SELECT mbrId FROM member m1
SELECT mbrId FROM member m1 WHERE mbrTp in ('MT001','MT002', 'MT003') AND estateCd = #{user.estateCd}
SELECT mbrId FROM member m1 WHERE mbrId = #{user.mbrId}
) order by frstRegDt desc

SELECT workNo , mbrId , custId , objtNo , workTitle , workContent , endDateYn , DATE_FORMAT(endDt, '%Y-%m-%d') as endDt , DATE_FORMAT(frstRegDt, '%Y-%m-%d') as frstRegDt , (select mbrNm from member m where w.frstRegUser = m.mbrId) mbrNm , frstRegUser , DATE_FORMAT(scheduledDt, '%Y-%m-%d') as scheduledDt , case when #{user.mbrTp} = 'MT004' then 'Y' ELSE ( case when w.frstRegUser = #{user.mbrId} then 'Y' else 'N' END ) END deleteBtnYn , (SELECT COUNT(*) FROM workdoc WHERE DATE_FORMAT(scheduledDt, '%Y-%m-%d') = DATE_FORMAT(w.scheduledDt, '%Y-%m-%d') AND frstRegUser in (
SELECT mbrId FROM member m1
SELECT mbrId FROM member m1 WHERE mbrTp in ('MT001','MT002', 'MT003') AND estateCd = #{user.estateCd}
SELECT mbrId FROM member m1 WHERE mbrId = #{user.mbrId}
) AND objtNo != '' ) objCnt , (SELECT COUNT(*) FROM workdoc WHERE DATE_FORMAT(scheduledDt, '%Y-%m-%d') = DATE_FORMAT(w.scheduledDt, '%Y-%m-%d') AND frstRegUser in (
SELECT mbrId FROM member m1
SELECT mbrId FROM member m1 WHERE mbrTp in ('MT001','MT002', 'MT003') AND estateCd = #{user.estateCd}
SELECT mbrId FROM member m1 WHERE mbrId = #{user.mbrId}
) AND custId != '' ) custCnt FROM workdoc w WHERE scheduledDt like #{selectedMonth} AND frstRegUser in ( SELECT mbrId FROM member m1
SELECT mbrId FROM member m1 WHERE mbrTp in ('MT001','MT002', 'MT003') AND estateCd = #{user.estateCd}
SELECT mbrId FROM member m1 WHERE mbrId = #{user.mbrId}
) order by frstRegDt desc

SELECT workNo , mbrId , custId , objtNo , workTitle , workContent , endDateYn , DATE_FORMAT(endDt, '%Y-%m-%d') as endDt , DATE_FORMAT(frstRegDt, '%Y-%m-%d') as frstRegDt , (select mbrNm from member m where w.frstRegUser = m.mbrId) mbrNm , frstRegUser , DATE_FORMAT(scheduledDt, '%Y-%m-%d') as scheduledDt , (TO_DAYS(now()) - TO_DAYS(scheduledDt)) as intvDay FROM workdoc w WHERE 1=1 and DATE_FORMAT(scheduledDt, '%Y-%m-%d') >= now() AND frstRegUser in ( SELECT mbrId FROM member m1
SELECT mbrId FROM member m1 WHERE mbrTp in ('MT001','MT002', 'MT003') AND estateCd = #{user.estateCd}
SELECT mbrId FROM member m1 WHERE mbrId = #{user.mbrId}
) order by intvDay desc
